package com.example.carapp;

import android.content.Intent;
import android.os.Bundle;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import com.example.carapp.databinding.ActivityMainBinding;
import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {
    private ActivityMainBinding binding;
    private List<CarBrand> carBrandList;
    private CarBrandAdapter carBrandAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        binding = ActivityMainBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());

        carBrandList = new ArrayList<>();
        initializeCarBrands();

        carBrandAdapter = new CarBrandAdapter(carBrandList, carBrand -> {
            Intent intent = new Intent(MainActivity.this, CarModelsActivity.class);
            intent.putExtra("carBrandName", carBrand.getName());
            startActivity(intent);
        });

        binding.recyclerViewBrands.setLayoutManager(new LinearLayoutManager(this));
        binding.recyclerViewBrands.setAdapter(carBrandAdapter);
    }

    private void initializeCarBrands() {
        carBrandList.add(new CarBrand(
                "BMW",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQz7JpWuGIbHdKPr6Kk7mh4E1P2aNnPvb1Ihg&s",
                "Баварский производитель автомобилей, известный своими роскошными и спортивными автомобилями."
        ));
        carBrandList.add(new CarBrand(
                "Mercedes",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQz7JpWuGIbHdKPr6Kk7mh4E1P2aNnPvb1Ihg&s",
                "Немецкий автопроизводитель, специализирующийся на роскошных и высококачественных автомобилях."
        ));
        carBrandList.add(new CarBrand(
                "Audi",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQz7JpWuGIbHdKPr6Kk7mh4E1P2aNnPvb1Ihg&s",
                "Известный немецкий бренд, который производит стильные и высокопроизводительные автомобили."
        ));
        carBrandList.add(new CarBrand(
                "Toyota",
                "data:image/jpeg;base64,",
                "Японский производитель автомобилей, известный своей надежностью и экономичностью."
        ));
        carBrandList.add(new CarBrand(
                "Ford",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQz7JpWuGIbHdKPr6Kk7mh4E1P2aNnPvb1Ihg&s",
                "Американская компания, производящая широкий спектр автомобилей, включая пикапы и внедорожники."
        ));
        carBrandList.add(new CarBrand(
                "Chevrolet",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQz7JpWuGIbHdKPr6Kk7mh4E1P2aNnPvb1Ihg&s",
                "Американский производитель, известный своими популярными моделями и доступными ценами."
        ));
        carBrandList.add(new CarBrand(
                "Nissan",
                "data:image/jpeg;base64,",
                "Японский автопроизводитель, который предлагает широкий выбор автомобилей, от седанов до внедорожников."
        ));
        carBrandList.add(new CarBrand(
                "Honda",
                "data:image/jpeg;base64,",
                "Японская компания, известная своими надежными и экономичными автомобилями."
        ));
        carBrandList.add(new CarBrand(
                "Hyundai",
                "data:image/jpeg;base64,",
                "Южнокорейский производитель, предлагающий широкий выбор доступных и высококачественных автомобилей."
        ));
        carBrandList.add(new CarBrand(
                "Volkswagen",
                "data:image/jpeg;base64,",
                "Немецкий автопроизводитель, известный своими инновациями и качественными автомобилями."
        ));
    }
}
